import pytest

def get_marker(input, marker_length):
    for i in range(len(input) - marker_length + 1):
        marker = input[i:(i+marker_length)]
        unique_characters = set(marker)
        if len(unique_characters) == marker_length:
            return i + marker_length

def get_first_packet_marker(input):
    marker_length = 4
    return get_marker(input, marker_length)

def get_first_message_marker(input):
    marker_length = 14
    return get_marker(input, marker_length)

def test_get_packet_marker():
    assert(get_first_packet_marker('bvwbjplbgvbhsrlpgdmjqwftvncz') == 5)
    assert(get_first_packet_marker('nppdvjthqldpwncqszvftbrmjlhg') == 6)
    assert(get_first_packet_marker('nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg') == 10)
    assert(get_first_packet_marker('zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw') == 11)

def test_get_message_marker():
    assert(get_first_message_marker('mjqjpqmgbljsphdztnvjfqwrcgsmlb') == 19)
    assert(get_first_message_marker('bvwbjplbgvbhsrlpgdmjqwftvncz') == 23)
    assert(get_first_message_marker('nppdvjthqldpwncqszvftbrmjlhg') == 23)
    assert(get_first_message_marker('nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg') == 29)
    assert(get_first_message_marker('zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw') == 26)

if __name__ == '__main__':
    input = ''
    print(f'Part 1: {get_first_packet_marker(input)}')
    print(f'Part 2: {get_first_message_marker(input)}')
